// plugin.js
tinymce.PluginManager.add("code",function(a){function b(){a.windowManager.open({title:"Source code",body:{type:"textbox",name:"code",multiline:true,minWidth:600,minHeight:500,value:a.getContent({source_view:true}),spellcheck:false},onSubmit:function(c){a.setContent(c.data.code)}})}a.addButton("code",{icon:"code",tooltip:"Source code",onclick:b});a.addMenuItem("code",{icon:"code",text:"Source code",context:"tools",onclick:b})});