// plugin.js
tinymce.PluginManager.add("example",function(b,a){b.addButton("example",{text:"My button",icon:false,onclick:function(){b.windowManager.open({title:"Example plugin",body:[{type:"textbox",name:"title",label:"Title"}],onsubmit:function(c){b.insertContent("Title: "+c.data.title)}})}});b.addMenuItem("example",{text:"Example plugin",context:"tools",onclick:function(){b.windowManager.open({title:"TinyMCE site",url:"http://www.tinymce.com",width:800,height:600,buttons:[{text:"Close",onclick:"close"}]})}})});