// Necessary javascript for admin-utilities in regards to the user system.
function update_user_table(){$(".listing .user-entry").each(function(){var b=$(this),a=b.find(".active-status");if(a.attr("data-useractive")=="1"){a.html('<a href="#" class="user-activate-link" title="Activated"><i class="icon-ok"></i></a>')}else{if(a.attr("data-useractive")=="-1"){a.html('<a href="#" class="user-activate-link" title="Deactivated"><i class="icon-times"></i></a>')}else{a.html('<a href="#" class="user-activate-link" title="Not Activated"><i class="icon-exclamation-sign"></i></a>')}}})}$(function(){update_user_table();$(".listing").on("click",".user-activate-link",function(){var b=$(this).closest(".active-status"),d=$(this).closest("tr"),a=b.attr("data-useractive"),c;if(a==0){c=1}else{if(a==1){c=-1}else{c=1}}$.ajax({url:Core.ROOT_URL+"user/activate.json",data:{user:d.attr("data-userid"),status:c},dataType:"json",type:"post",success:function(e){b.attr("data-useractive",c);update_user_table()}});return false})});